// Tips Configuration
// Update these links with your actual payment information

export const tipsConfig = {
  // PayPal - Replace 'yourusername' with your PayPal.me username
  // Example: https://paypal.me/johndoe
  paypal: "https://paypal.me/yourusername",

  // Venmo - Replace 'yourusername' with your Venmo username
  // Example: https://venmo.com/johndoe
  venmo: "https://venmo.com/yourusername",

  // Zelle - Replace with your email address
  // Users will email you to request Zelle details
  zelleEmail: "your-email@example.com",

  // Cash App - Optional, uncomment and add your Cash App link
  // cashapp: "https://cash.app/$yourusername",

  // Bitcoin - Optional, uncomment and add your Bitcoin address
  // bitcoin: "bc1qexamplebitcoinaddress",

  // Custom message
  message: "Every tip helps improve the AI accuracy and keeps the service running! üôè"
};

// Alternative: You can also add these as environment variables in production
// and use process.env.REACT_APP_PAYPAL_URL, etc.
