import React, { useState, useCallback } from 'react';
import { 
  Container, 
  Box, 
  Typography, 
  Paper, 
  CircularProgress,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  Divider,
  Card,
  CardMedia,
  Button
} from '@mui/material';
import { useDropzone } from 'react-dropzone';
import imageCompression from 'browser-image-compression';
import axios from 'axios';
import ReactMarkdown from 'react-markdown';
import TipsSection from './components/TipsSection';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001/api';

function App() {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [result, setResult] = useState(null);
  const [history, setHistory] = useState(() => {
    const saved = localStorage.getItem('watchHistory');
    return saved ? JSON.parse(saved) : [];
  });
  const [guessAgainLoading, setGuessAgainLoading] = useState(false);

  const onDrop = useCallback(async (acceptedFiles) => {
    try {
      setLoading(true);
      setError(null);

      const file = acceptedFiles[0];

      // Compress image
      const options = {
        maxSizeMB: 1,
        maxWidthOrHeight: 1920,
        useWebWorker: true
      };

      const compressedFile = await imageCompression(file, options);

      // Create form data
      const formData = new FormData();
      formData.append('image', compressedFile);

      // Send to backend
      const response = await axios.post(`${API_URL}/analyze`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      const newResult = {
        ...response.data,
        image: URL.createObjectURL(file),
        file: compressedFile
      };

      setResult(newResult);

      // Update history
      const newHistory = [newResult, ...history].slice(0, 10);
      setHistory(newHistory);
      localStorage.setItem('watchHistory', JSON.stringify(newHistory));

    } catch (err) {
      setError(err.response?.data?.error || 'Failed to analyze image');
    } finally {
      setLoading(false);
    }
  }, [history]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.webp']
    },
    maxFiles: 1
  });

  const handleGuessAgain = async () => {
    if (!result || !result.file) return;
    setGuessAgainLoading(true);
    setError(null);
    try {
      const formData = new FormData();
      formData.append('image', result.file);
      formData.append('guessAgain', 'true');
      const response = await axios.post(`${API_URL}/analyze`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setResult({ ...response.data, image: result.image, file: result.file });
      setHistory([{ ...response.data, image: result.image, file: result.file }, ...history]);
    } catch (err) {
      console.error('Guess again error:', err);
      setError('Failed to guess again: ' + (err.response?.data?.error || err.message));
    } finally {
      setGuessAgainLoading(false);
    }
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ my: 4 }}>
        <Typography variant="h3" component="h1" gutterBottom align="center">
          WhatRef.ai
        </Typography>

        <Paper
          {...getRootProps()}
          sx={{
            p: 3,
            textAlign: 'center',
            cursor: 'pointer',
            bgcolor: isDragActive ? 'action.hover' : 'background.paper',
            border: '2px dashed',
            borderColor: isDragActive ? 'primary.main' : 'divider',
            mb: 4
          }}
        >
          <input {...getInputProps()} />
          <Typography>
            {isDragActive
              ? "Drop the image here"
              : "Drag and drop a wristwatch image here, or click to select"}
          </Typography>
        </Paper>

        {loading && (
          <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
            <CircularProgress />
          </Box>
        )}

        {error && (
          <Typography color="error" align="center" sx={{ my: 2 }}>
            {error}
          </Typography>
        )}

        {result && (
          <Card sx={{ display: 'flex', p: 2, mb: 2 }}>
            <CardMedia
              component="img"
              image={result.image}
              alt="Analyzed wristwatch"
              sx={{ width: 350, height: 350, objectFit: 'cover', mr: 2, borderRadius: 2 }}
            />
            <Box sx={{ flex: 1 }}>
              <Typography variant="h5" gutterBottom>
                Analysis Result
              </Typography>
              <Box sx={{ mb: 1 }}>
                <ReactMarkdown>{result.result}</ReactMarkdown>
              </Box>
              <Button
                variant="outlined"
                onClick={handleGuessAgain}
                disabled={guessAgainLoading}
                sx={{ mb: 1 }}
              >
                {guessAgainLoading ? 'Guessing Again...' : 'Guess Again'}
              </Button>
              <Typography variant="body2" color="text.secondary">
                Analyzed on: {new Date(result.timestamp).toLocaleString()}
              </Typography>
            </Box>
          </Card>
        )}

        {history.length > 0 && (
          <Paper sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              Recent Analyses
            </Typography>
            <List>
              {history.map((item, index) => (
                <React.Fragment key={item.timestamp}>
                  <ListItem alignItems="flex-start" sx={{ py: 2 }}>
                    <ListItemAvatar sx={{ minWidth: 100 }}>
                      <Box
                        component="img"
                        src={item.image}
                        alt="Watch thumbnail"
                        sx={{
                          width: 100,
                          height: 100,
                          objectFit: 'cover',
                          borderRadius: 1,
                          boxShadow: 1
                        }}
                      />
                    </ListItemAvatar>
                    <ListItemText
                      primary={
                        <Box sx={{ mb: 1 }}>
                          <ReactMarkdown>{item.result}</ReactMarkdown>
                        </Box>
                      }
                      secondary={
                        <Typography
                          component="span"
                          variant="body2"
                          color="text.secondary"
                        >
                          {new Date(item.timestamp).toLocaleString()}
                        </Typography>
                      }
                      sx={{ ml: 2 }}
                    />
                  </ListItem>
                  {index < history.length - 1 && <Divider variant="fullWidth" component="li" />}
                </React.Fragment>
              ))}
            </List>
          </Paper>
        )}

        {/* Tips Section */}
        <TipsSection />
      </Box>
    </Container>
  );
}

export default App;
